import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import Navbar from './components/Navbar';

const App = () => {
  const [isTopBarVisible, setIsTopBarVisible] = useState(true);
  const [lastScrollY, setLastScrollY] = useState(0);

  // Carousel state for News & Events (non-destructive)
  const newsGridRef = React.useRef(null);
  const [currentNewsSlide, setCurrentNewsSlide] = useState(0);
  const [newsSlidesCount, setNewsSlidesCount] = useState(0);
  const [isNewsAutoPlay, setIsNewsAutoPlay] = useState(true);

  // Scroll-based navbar visibility
  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;

      if (currentScrollY > lastScrollY && currentScrollY > 100) {
        // Scrolling down and past initial 100px
        setIsTopBarVisible(false);
      } else if (currentScrollY < lastScrollY && currentScrollY < 50) {
        // Scrolling up and almost at the top (within 50px)
        setIsTopBarVisible(true);
      }

      setLastScrollY(currentScrollY);
    };

    window.addEventListener('scroll', handleScroll, { passive: true });

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [lastScrollY]);

  // Initialize News & Events carousel without altering existing markup
  useEffect(() => {
    const grid = document.querySelector('.news-section .news-grid');
    if (!grid) return;

    newsGridRef.current = grid;

    // Count slides
    const cards = Array.from(grid.children).filter(el => el.classList && el.classList.contains('news-card'));
    setNewsSlidesCount(cards.length);

    // Prepare grid to behave like a carousel
    grid.style.display = 'flex';
    grid.style.flexWrap = 'nowrap';
    grid.style.overflow = 'hidden';
    grid.style.gap = '0px';
    grid.style.transition = 'transform 500ms ease';
    grid.style.willChange = 'transform';

    // Size each card to full width
    cards.forEach(card => {
      card.style.flex = '0 0 100%';
      card.style.maxWidth = '100%';
      card.style.boxSizing = 'border-box';
    });

    // Accessibility attributes
    grid.setAttribute('role', 'region');
    grid.setAttribute('aria-roledescription', 'carousel');
    grid.setAttribute('aria-label', 'News & Events');

    const handleMouseEnter = () => setIsNewsAutoPlay(false);
    const handleMouseLeave = () => setIsNewsAutoPlay(true);

    grid.addEventListener('mouseenter', handleMouseEnter);
    grid.addEventListener('mouseleave', handleMouseLeave);

    const handleResize = () => {
      // Re-apply transform on resize to keep alignment
      requestAnimationFrame(() => {
        if (newsGridRef.current) {
          newsGridRef.current.style.transform = `translateX(-${currentNewsSlide * 100}%)`;
        }
      });
    };

    window.addEventListener('resize', handleResize);

    return () => {
      grid.removeEventListener('mouseenter', handleMouseEnter);
      grid.removeEventListener('mouseleave', handleMouseLeave);
      window.removeEventListener('resize', handleResize);
    };
  }, [currentNewsSlide]);

  // Update transform when slide changes
  useEffect(() => {
    if (!newsGridRef.current) return;
    newsGridRef.current.style.transform = `translateX(-${currentNewsSlide * 100}%)`;
  }, [currentNewsSlide]);

  // Autoplay
  useEffect(() => {
    if (!isNewsAutoPlay || newsSlidesCount <= 1) return;
    const intervalId = setInterval(() => {
      setCurrentNewsSlide(prev => (prev + 1) % Math.max(newsSlidesCount, 1));
    }, 5000);
    return () => clearInterval(intervalId);
  }, [isNewsAutoPlay, newsSlidesCount]);

  const goToNewsSlide = (index) => {
    if (newsSlidesCount === 0) return;
    const bounded = (index % newsSlidesCount + newsSlidesCount) % newsSlidesCount;
    setCurrentNewsSlide(bounded);
  };

  const nextNewsSlide = () => {
    if (newsSlidesCount === 0) return;
    setCurrentNewsSlide(prev => (prev + 1) % newsSlidesCount);
  };

  const prevNewsSlide = () => {
    if (newsSlidesCount === 0) return;
    setCurrentNewsSlide(prev => (prev - 1 + newsSlidesCount) % newsSlidesCount);
  };

  return (
    <div className="App">
      <Navbar isTopBarVisible={isTopBarVisible} />

      {/* Home Section */}
      <section id="home" className={`section home-section ${!isTopBarVisible ? 'navbar-collapsed' : ''}`}>
        <div className="container">
          <div className="hero-content">
            {/* <h1 className="hero-title">CITY COLLEGE OF BAYAWAN</h1>
            <p className="hero-subtitle">Honor and Excellence for the Highest Good</p>
            <p className="hero-motto">Honus et Excellentia Ad Summum Bonum</p> */}
            <div className="hero-buttons">
              <a href="#academics" className="btn btn-primary">Explore Programs</a>
              <a href="#admissions" className="btn btn-secondary">Apply Now</a>
            </div>
          </div>
        </div>
      </section>

      {/* Academic Programs Section */}
      <section id="academics" className="section academics-section">
        <div className="container">
          <h2 className="section-title">Academic Programs</h2>
          <p className="section-subtitle">Discover our comprehensive range of academic programs designed to prepare you for success</p>

          <div className="programs-grid">
            <div className="program-card">
              <div className="program-icon">
                <svg viewBox="0 0 24 24" width="48" height="48" fill="currentColor">
                  <path d="M12 3L1 9l11 6 9-4.91V17h2V9L12 3zm0 13L3.74 11 12 6.82 20.26 11 12 16z"/>
                </svg>
              </div>
              <h3>Bachelor of Science in Business Administration</h3>
              <p>Develop essential business skills and knowledge for leadership roles in various industries.</p>
              <ul>
                <li>Financial Management</li>
                <li>Marketing Management</li>
                <li>Human Resource Management</li>
                <li>Operations Management</li>
              </ul>
            </div>

            <div className="program-card">
              <div className="program-icon">
                <svg viewBox="0 0 24 24" width="48" height="48" fill="currentColor">
                  <path d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"/>
                </svg>
              </div>
              <h3>Bachelor of Science in Information Technology</h3>
              <p>Master the latest technologies and prepare for careers in the digital world.</p>
              <ul>
                <li>Software Development</li>
                <li>Database Management</li>
                <li>Network Administration</li>
                <li>Web Development</li>
              </ul>
            </div>

            <div className="program-card">
              <div className="program-icon">
                <svg viewBox="0 0 24 24" width="48" height="48" fill="currentColor">
                  <path d="M19 3H5c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h14c1.1 0 2-.9 2-2V5c0-1.1-.9-2-2-2zm-5 14H7v-2h7v2zm3-4H7v-2h10v2zm0-4H7V7h10v2z"/>
                </svg>
              </div>
              <h3>Bachelor of Science in Education</h3>
              <p>Shape the future by becoming an educator and inspiring the next generation.</p>
              <ul>
                <li>Elementary Education</li>
                <li>Secondary Education</li>
                <li>Special Education</li>
                <li>Physical Education</li>
              </ul>
            </div>

            <div className="program-card">
              <div className="program-icon">
                <svg viewBox="0 0 24 24" width="48" height="48" fill="currentColor">
                  <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z"/>
                </svg>
              </div>
              <h3>Bachelor of Science in Hospitality Management</h3>
              <p>Excel in the dynamic world of hospitality and tourism management.</p>
              <ul>
                <li>Hotel Operations</li>
                <li>Food and Beverage</li>
                <li>Tourism Management</li>
                <li>Event Planning</li>
              </ul>
            </div>
          </div>

          <div className="section-cta">
            <a href="/academics" className="btn btn-primary">Learn More About Academic Programs</a>
          </div>
        </div>
      </section>

      {/* Admissions Section */}
      <section id="admissions" className="section admissions-section">
        <div className="container">
          <h2 className="section-title">Admissions</h2>
          <p className="section-subtitle">Start your journey towards academic excellence</p>

          <div className="admissions-content">
            <div className="admissions-info">
              <h3>Admission Requirements</h3>
              <ul>
                <li>Completed Application Form</li>
                <li>High School Diploma or Equivalent</li>
                <li>Official Transcript of Records</li>
                <li>Certificate of Good Moral Character</li>
                <li>Medical Certificate</li>
                <li>2x2 ID Pictures (4 copies)</li>
                <li>Birth Certificate (NSO/PSA)</li>
              </ul>
            </div>

            <div className="admissions-process">
              <h3>Application Process</h3>
              <div className="process-steps">
                <div className="step">
                  <div className="step-number">1</div>
                  <div className="step-content">
                    <h4>Submit Application</h4>
                    <p>Complete and submit your application form with all required documents</p>
                  </div>
                </div>
                <div className="step">
                  <div className="step-number">2</div>
                  <div className="step-content">
                    <h4>Entrance Examination</h4>
                    <p>Take the college entrance examination on the scheduled date</p>
                  </div>
                </div>
                <div className="step">
                  <div className="step-number">3</div>
                  <div className="step-content">
                    <h4>Interview</h4>
                    <p>Attend the scheduled interview with the admissions committee</p>
                  </div>
                </div>
                <div className="step">
                  <div className="step-number">4</div>
                  <div className="step-content">
                    <h4>Enrollment</h4>
                    <p>Complete enrollment procedures and pay necessary fees</p>
                  </div>
                </div>
              </div>
            </div>
          </div>

          <div className="section-cta">
            <a href="/admissions" className="btn btn-primary">Learn More About Admissions</a>
          </div>
        </div>
      </section>

      {/* News & Events Section */}
      <section id="news" className="section news-section">
        <div className="container">
          <h2 className="section-title">News & Events</h2>
          <p className="section-subtitle">Stay updated with the latest happenings at City College of Bayawan</p>

          <div className="news-grid">
            <article className="news-card">
              <div className="news-image">
                <div className="news-placeholder"></div>
              </div>
              <div className="news-content">
                <div className="news-date">December 15, 2024</div>
                <h3>Annual Recognition Day 2024</h3>
                <p>Join us in celebrating the achievements of our outstanding students during the Annual Recognition Day ceremony.</p>
                <button className="read-more">Read More</button>
              </div>
            </article>

            <article className="news-card">
              <div className="news-image">
                <div className="news-placeholder"></div>
              </div>
              <div className="news-content">
                <div className="news-date">December 10, 2024</div>
                <h3>New Computer Laboratory Opening</h3>
                <p>State-of-the-art computer laboratory equipped with the latest technology for IT students.</p>
                <button className="read-more">Read More</button>
              </div>
            </article>

            <article className="news-card">
              <div className="news-image">
                <div className="news-placeholder"></div>
              </div>
              <div className="news-content">
                <div className="news-date">December 5, 2024</div>
                <h3>Sports Festival 2024</h3>
                <p>Annual sports festival featuring various athletic competitions and team building activities.</p>
                <button className="read-more">Read More</button>
              </div>
            </article>
          </div>

          {/* Non-destructive carousel controls for News & Events */}
          <div className="news-controls" style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', gap: '16px', marginTop: '16px' }}>
            <button
              type="button"
              aria-label="Previous news"
              onClick={prevNewsSlide}
              style={{
                padding: '10px 14px',
                borderRadius: '8px',
                border: '1px solid #d0d5dd',
                background: '#fff',
                color: '#111827',
                cursor: newsSlidesCount > 1 ? 'pointer' : 'default',
                opacity: newsSlidesCount > 1 ? 1 : 0.5
              }}
              disabled={newsSlidesCount <= 1}
            >

            </button>

            <div className="news-dots" aria-label="News slides" role="tablist" style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
              {Array.from({ length: Math.max(newsSlidesCount, 0) }).map((_, idx) => (
                <button
                  key={idx}
                  type="button"
                  role="tab"
                  aria-selected={currentNewsSlide === idx}
                  aria-label={`Go to slide ${idx + 1}`}
                  onClick={() => goToNewsSlide(idx)}
                  style={{
                    width: '10px',
                    height: '10px',
                    borderRadius: '9999px',
                    border: 'none',
                    background: currentNewsSlide === idx ? '#111827' : '#d1d5db',
                    transform: currentNewsSlide === idx ? 'scale(1.1)' : 'none',
                    transition: 'background 200ms ease, transform 200ms ease',
                    cursor: 'pointer'
                  }}
                />
              ))}
            </div>

            <button
              type="button"
              aria-label="Next news"
              onClick={nextNewsSlide}
              style={{
                padding: '10px 14px',
                borderRadius: '8px',
                border: '1px solid #d0d5dd',
                background: '#fff',
                color: '#111827',
                cursor: newsSlidesCount > 1 ? 'pointer' : 'default',
                opacity: newsSlidesCount > 1 ? 1 : 0.5
              }}
              disabled={newsSlidesCount <= 1}
            >

            </button>
          </div>

          <div className="section-cta">
            <a href="/news" className="btn btn-primary">View All News & Events</a>
          </div>
        </div>
      </section>

      {/* Downloads Section */}
      <section id="downloads" className="section downloads-section">
        <div className="container">
          <h2 className="section-title">Downloads</h2>
          <p className="section-subtitle">Access important documents and forms</p>

          <div className="downloads-grid">
            <div className="download-category">
              <h3>Student Forms</h3>
              <div className="download-links">
                <button className="download-link">
                  <svg viewBox="0 0 24 24" width="20" height="20" fill="currentColor">
                    <path d="M14 2H6a2 2 0 00-2 2v16a2 2 0 002 2h12a2 2 0 002-2V8l-6-6z"/>
                    <path d="M14 2v6h6"/>
                    <path d="M16 13H8"/>
                    <path d="M16 17H8"/>
                    <path d="M10 9H8"/>
                  </svg>
                  Application Form
                </button>
                <button className="download-link">
                  <svg viewBox="0 0 24 24" width="20" height="20" fill="currentColor">
                    <path d="M14 2H6a2 2 0 00-2 2v16a2 2 0 002 2h12a2 2 0 002-2V8l-6-6z"/>
                    <path d="M14 2v6h6"/>
                    <path d="M16 13H8"/>
                    <path d="M16 17H8"/>
                    <path d="M10 9H8"/>
                  </svg>
                  Enrollment Form
                </button>
                <button className="download-link">
                  <svg viewBox="0 0 24 24" width="20" height="20" fill="currentColor">
                    <path d="M14 2H6a2 2 0 00-2 2v16a2 2 0 002 2h12a2 2 0 002-2V8l-6-6z"/>
                    <path d="M14 2v6h6"/>
                    <path d="M16 13H8"/>
                    <path d="M16 17H8"/>
                    <path d="M10 9H8"/>
                  </svg>
                  Student Handbook
                </button>
              </div>
            </div>

            <div className="download-category">
              <h3>Academic Calendar</h3>
              <div className="download-links">
                <button className="download-link">
                  <svg viewBox="0 0 24 24" width="20" height="20" fill="currentColor">
                    <path d="M14 2H6a2 2 0 00-2 2v16a2 2 0 002 2h12a2 2 0 002-2V8l-6-6z"/>
                    <path d="M14 2v6h6"/>
                    <path d="M16 13H8"/>
                    <path d="M16 17H8"/>
                    <path d="M10 9H8"/>
                  </svg>
                  Academic Calendar 2024-2025
                </button>
                <button className="download-link">
                  <svg viewBox="0 0 24 24" width="20" height="20" fill="currentColor">
                    <path d="M14 2H6a2 2 0 00-2 2v16a2 2 0 002 2h12a2 2 0 002-2V8l-6-6z"/>
                    <path d="M14 2v6h6"/>
                    <path d="M16 13H8"/>
                    <path d="M16 17H8"/>
                    <path d="M10 9H8"/>
                  </svg>
                  Class Schedule
                </button>
              </div>
            </div>

            <div className="download-category">
              <h3>Policies & Guidelines</h3>
              <div className="download-links">
                <button className="download-link">
                  <svg viewBox="0 0 24 24" width="20" height="20" fill="currentColor">
